const fs = require('fs');
exports.mime =function (y) {
    
    return new Promise((resolve, reject) => {
        fs.readFile('./node_modules/mime/mime.json', (err, data) => {
            if(! err) {
                
                var x =JSON.parse(data.toString());
                resolve(x[y]);
            }else {
                reject('');
            }
            
        })
    })
}